//SPDX-License-Identifier: UNLICENCED
pragma solidity ^0.8.0;

import "./ICHN.sol";

contract PaymentProcessor {

    ICHN public chn;
    address public admin;

    address [] public winnersAddresses;
    mapping (address => uint256) public winnersMapping;
    mapping (address => uint256) public stakersMapping;

    event StakeSuccessful(address user,  uint amount);
    event PayoutDone(uint256 totalWinners, uint256 totalPayoutAmount);

    constructor (address _admin, address _chnAddress) {
        chn = ICHN(_chnAddress);
        admin = _admin;
    }

    function setNewMerchant(address _admin) public returns(bool){
        require(msg.sender == admin);
        admin = _admin;
        return true;
    }

    function makeStake(uint _amount) public returns(bool){
        chn.approve(address(this), _amount);
        chn.transferFrom(msg.sender, admin, _amount);
        stakersMapping[msg.sender] = _amount;
        emit StakeSuccessful(msg.sender, _amount);
        return true;
    }


    function setWinners(uint256 amount) public {
        require(stakersMapping[msg.sender] > 0);
        winnersMapping[msg.sender] = amount;
        winnersAddresses.push(msg.sender);
        stakersMapping[msg.sender] = 0;
    }

    function payoutWinners() public {
        require(msg.sender == admin, "only admin can pay winners");
        uint256 totalPayoutAmount = getPendingPayoutAmount();
        chn.approve(address(this), totalPayoutAmount);
        for (uint i=0; i < winnersAddresses.length; i++){
            chn.transferFrom(admin, winnersAddresses[i], winnersMapping[winnersAddresses[i]]);
            winnersMapping[winnersAddresses[i]] = 0;
        }
        delete winnersAddresses;
        emit PayoutDone(winnersAddresses.length, totalPayoutAmount);
        
    }

    function getPendingPayoutAmount() public view returns(uint256){
        uint256 totalPayoutAmount = 0;
        for (uint i=0; i < winnersAddresses.length; i++){
            totalPayoutAmount += winnersMapping[winnersAddresses[i]];
        }
        return totalPayoutAmount;
    }

}
